// --strictNullChecks

import * as React from 'react'

import { Form, Modal, Label } from 'react-bootstrap'

import Header from '../../components/header'
import Footer from '../../components/footer'
import Select from '../../components/select'
import BarChart from '../../components/charts/bar'

import './style.css'

import { OptionInterface } from '../../interfaces/option'
import { StockInterface } from '../../interfaces/stock'
import { ProductionOrderInterface } from '../../interfaces/production_order';

import connect from './connect'

interface PropTypes {

    cost_raw_material: number,
    order: any

    fetchCostRawMaterial: any,
    setSelectedOrder: any,
}

class OperacionalPage extends React.Component<PropTypes, any> {

    constructor(props: PropTypes) {
        super(props)

        this.state = {
            selected_value: { id: 1, quantidade: 25000 },
        }

        this.handleSelect = this.handleSelect.bind(this)
    }

    componentWillMount() {
        this.props.fetchCostRawMaterial()
    }

    handleSelect(event: any) {
        this.props.setSelectedOrder(event.target.value)
    }

    processQuant() {
        return [{ name: this.state.selected_value.id, estoque: 2000, quantidade: this.props.order }]
    }

    render() {
        return (
            <div>
                <Header />
                <div className={'container'}>
                    <div>
                        <Form>
                            <Select
                                label={'Ordem de produção'}
                                control_id={''}
                                handleSelect={this.handleSelect}
                            />
                        </Form>
                    </div>
                    <div id={'content'}>
                        <div className="static-modal">
                            <Modal.Dialog>
                                <Modal.Header>
                                    <Modal.Title>Detalhes da Ordem de Produção</Modal.Title>
                                </Modal.Header>

                                <Modal.Body>
                                    <p>
                                        <span>id:</span>
                                        <span>{this.state.selected_value.id}</span>
                                    </p>
                                    <p>
                                        <span>quantidade:</span>
                                        <span>{this.state.selected_value.id}</span>
                                    </p>
                                    <p>
                                        <span>data:</span>
                                        <span>{this.state.selected_value.id}</span>
                                    </p>
                                    <p>
                                        <span>cliente:</span>
                                        <span>{this.state.selected_value.id}</span>
                                    </p>
                                </Modal.Body>

                            </Modal.Dialog>
                        </div>
                    </div>
                    <BarChart
                        width={500}
                        heigth={500}
                        xname={'quant'}
                        data_key={[{
                            key: 'estoque', color: 'blue',
                        }, { key: 'quantidade', color: 'red' }]}
                        data={this.processQuant()}
                    />
                </div>
                <Footer />
            </div>
        )
    }
}

export default connect(OperacionalPage)
