import * as React from 'react'

import { Form } from 'react-bootstrap'

import Header from '../../components/header'
import Footer from '../../components/footer'
import Select from '../../components/select'
import BarChart from '../../components/charts/bar'

import { OptionInterface } from '../../interfaces/option'
import { StockInterface } from '../../interfaces/stock'
import { ProductionOrderInterface } from '../../interfaces/production_order';

import connect from './connect'

interface PropTypes {

    cost_raw_material: number,

    fetchCostRawMaterial: any
}

class OperacionalPage extends React.Component<PropTypes, any> {

    constructor(props: PropTypes) {
        super(props)

        this.state = {
            selected_value: { id: 1, quant: 25000 },
        }
    }

    componentWillMount() {
        this.props.fetchCostRawMaterial()
    }

    handleSelect(value: ProductionOrderInterface) {
        this.setState({ selected_value: value }, () => { console.log(this.state) })
    }

    processQuant() {
        return [{ name: this.state.selected_value.id, st: 2000, or: this.state.selected_value.quantidade }]
    }

    render() {
        return (
            <div>
                <div>
                    <Form>
                        <Select
                            label={'Item'}
                            control_id={''}
                            handleSelect={this.handleSelect}
                        />
                    </Form>
                    <BarChart
                        width={500}
                        heigth={500}
                        xname={'quant'}
                        data_key={[{
                            key: 'st', color: 'blue',
                        }, { key: 'or', color: 'red' }]}
                        data={this.processQuant()}
                    />
                </div>
                <Footer />
            </div>
        )
    }
}

export default connect(OperacionalPage)
